B = 1;		else			V = Data[bookId][1]			B = 0;		end		--第一为  值，第2为是类型		return V, B;	end;	RefreshTime = "06:00";	--杀怪增加经验倍数	AddExpParam = function(bookId, objPlayer)		local dwRand = math.random(10000);		local BeiShu = 0;		if (dwRand <= 300) then			BeiShu = Exp[bookId];		end		return BeiShu;	end;}