	GetShowSort = function(key)
		local index = 0;
		if key == "dwAttack" then index = 1;
		elseif key == "dwDefense" then index = 2;
		elseif key == "dwHPMax" then index = 3;
		elseif key == "dwFlee" then index = 4;
		elseif key == "dwCrit" then index = 5;
		elseif key == "dwCritDown" then index = 6;
		elseif key == "dwPoJiaValue" then index = 7;
		elseif key == "dwPoJiaDiKang" then index = 8;
		
		end
		return index,_G.QunXiaLuAttrText[key];
	end;
	
	--获取属性个数
	GetAttrCount = function()
		local count = 0;
		for i,v in pairs(QunXiaLuAttrText) do
			if type(v) ~= 'function' then
				count = count + 1;
			end
		end
		return count;
	end;
};

