[12061]={id=12061,name='三思不解',_type=0,cancer=0,des='打退了那些惊恐的猞猁，你觉得它们像是受了惊，里面怕是有不得了的东西。阿德南阿訇向你们讲起了一段传说，黑将军的一双儿女年满六岁时，收养了一条长着两个头，却共用一个身体的小蟒蛇。这双头蟒与黑将军的两个孩子感情非常深厚，就连他们被黑将军埋入枯井时，那双头蟒也追随地下，不曾背离，既然它还活着，那这里一定它经常出没之地。',accept_talk=33,uncomplete_talk=0,complete_talk=36,npc_talk=0,need_lv=117,need_task=12060,need_item=0,need_sp=0,repeat_type=0,repeat_times=0,repeat_accept=1,auto_accept=0,auto_complete=0,team_share=0,give_item=0,give_num=0,monster_id1='0',monster_num1=0,monster_id2='0',monster_num2=0,monster_id3='0',monster_num3=0,monster_id4='0',monster_num4=0,item_id1=0,item_num1=0,item_id2=0,item_num2=0,item_id3=0,item_num3=0,item_id4=0,item_num4=0,special_need1=0,special_num1=0,map_id=0,x=0,y=0,round=0,use_item=0,use_time=0,money=109800,exp=150000000,pneuma=819,item1_id=0,item1_num=0,item2_id=0,item2_num=0,pro_item1=0,pro_item2=0,npc_need1=0,npc_item=0,npc_item_num=0,text_doing='0',text_finish='与阿德南阿訇对话',accept_npc='阿德南阿訇',accept_map=5119,accept_x=4,accept_y=-23,accept_map2=0,accept_x2=0,accept_y2=0,finish_npc='阿德南阿訇',finish_map=5119,finish_x=4,finish_y=-23,target_text='0',target_map=0,target_x=0,target_y=0,link_str1='0',str1_map=0,str1_x=0,str1_y=0,link_str2='0',str2_map=0,str2_x=0,str2_y=0,link_str3='0',str3_map=0,str3_x=0,str3_y=0,link_str4='0',str4_map=0,str4_x=0,str4_y=0,link_str5='0',link_fun=0,link_str6='0',link_fun1=0,link_str7='0',link_fun2=0,link_str8='0',link_fun3=0,lead_on_accept=0,lead_on_arrive=0,lead_on_can_fihish=0,lead_on_finish=0,},
