--战场WarAddition[10060] = {};local Form = WarAddition[10060]Form.Base = {	Config = {		--real 该场景将产生几个真实坐标，Proceed前往位置,Return返回位置 		[8080] = {Real = 4,Proceed = 8081,Return = 8080};		[8081] = {Real = 3,Proceed = 8082,Return = 8080};		[8082] = {Real = 2,Proceed = 8083,Return = 8081};		[8083] = {Real = 1,Proceed = 8084,Return = 8082};		[8084] = {Real = 1,Proceed = 8085,Return = 8083};		[8085] = {Real = 1,Proceed = 8086,Return = 8084};		[8086] = {Real = 1,Proceed = 8087,Return = 8085};		[8087] = {Real = 1,Proceed = 8088,Return = 8086};		[8088] = {Real = 1,Proceed = 8089,Return = 8086};		[8089] = {Real = 1,Proceed = nil,Return = 8088};	};	Award = {		[1] = {Exp = 8000000,ZhenQi = 20000,YinLiang = 1500000,LiJin = 1000,Mail = 13009101};		[2] = {Exp = 3200000,ZhenQi = 10000,YinLiang = 600000,LiJin = 600,Mail = 13009102};		[3] = {Exp = 1800000,ZhenQi = 5000,YinLiang = 300000,LiJin = 400,Mail = 13009103};		[4] = {Exp = 1200000,ZhenQi = 2000,YinLiang = 200000,LiJin = 200,Mail = 13009104};	};};--随机位置function Form:Random(Data)	local place = {1,2,3,4};	local a = {};	for loop = 1,Data.Real do		local dwRand = math.random(#place);		a[place[dwRand]] = place[dwRand]		table.remove(place,dwRand)	end	return a;end	--返回真确位置function Form:Count(objWar)	objWar.location = {};	for k,n in pairs(self.Base.Config) do		objWar.location[k] = {};		objWar.location[k] = self:Random(n)	endend--活动奖励function Form:Award(objWar,objPlayer,objWarSystem,Ranking)	local t = self.Base.Award[Ranking]	local dwRoleID = objPlayer:GetRoleID()	local tbInfo = {};	tbInfo.jingyan = math.floor(t.Exp);	tbInfo.zhenqi_G = math.floor(t.ZhenQi);	tbInfo.zhenqi_M = math.floor(t.ZhenQi);	tbInfo.zhenqi_S = math.floor(t.ZhenQi);	tbInfo.zhenqi_H = math.floor(t.ZhenQi);	tbInfo.zhenqi_T = math.floor(t.ZhenQi);	objWarSystem:AddHarvest(tbInfo);	objWarSystem:AddMoneyHarvest(t.YinLiang,0,t.LiJin)	CMailManager:SendConfigMail(t.Mail, dwRoleID)endfunction Form:Convey(objMapSys,dwID,Data,MapId)	local m = self.Base.Config[MapId]	if (dwID == Data[dwID]) then		if not objMapSys:ExecTransforMapXY(m.Proceed,-10.54,-8.73,5.5) then			return false;		end;	else		if not objMapSys:ExecTransforMapXY(m.Return,-10.54,-8.73,5.5) then			return false;		end;	end	return trueend