--[[功能：成就系统类管理类（服务器）版本：v1.0作者：粟宇征时间：2013-02-22]]_G.CAchievementSystemManager = {};_G.CAchievementSystemManager = CSingle:new();CSingleManager:AddSingle(CAchievementSystemManager);-----------------------------------------------------------------------------------------------function CAchievementSystemManager:Create()	self.tabHideAchievementList = {}	CKernelApp.KSInitHideAchievementListMsg{ LineID = CKernelApp.dwLineID }	return true;end;function CAchievementSystemManager:Update(dwInterval)	return true;end;function CAchievementSystemManager:Destroy()end;-------------------------------------------------------------------------------------------------接口-----------------------------------------------------------------------------------------------function CAchievementSystemManager:SetHideAchievementList(tabData)	self.tabHideAchievementList = tabData;endfunction CAchievementSystemManager:GetHideAchievementList()	return self.tabHideAchievementList;endfunction CAchievementSystemManager:AddHideAchievement(dwAchievementID,dwRoleID,szRoleName)	local tabHideAchievement = {};	tabHideAchievement.dwAchievementID = dwAchievementID;	tabHideAchievement.dwRoleID = dwRoleID;	tabHideAchievement.szRoleName = szRoleName;	self.tabHideAchievementList[dwAchievementID] = tabHideAchievement;		local tabInfo = {}	tabInfo.szRoleName = szRoleName	local tabConfig = AchievementConfig[dwAchievementID]	tabInfo.szAchievementName = tabConfig.tabContent.szName	local szText = AchievementBroadCastConfig.szTextyc	szText = CAchievementSystem:ParseBroadCast(szText,tabInfo)	for i = 1,3 do		for _,objPlayer in pairs(CMapManager:GetPlayer()) do			objPlayer.OnAchievementBroadCastMsg { RoleID = dwRoleID,Text = szText }			objPlayer.OnOpenHideAchievementMsg { tabData = tabHideAchievement }		end	endendfunction CAchievementSystemManager:IsHaveHideAchievement(dwAchievementID)	if self.tabHideAchievementList[dwAchievementID] then return true end;end-----------------