--[[	--功能：聚灵珠等级数据库类	--作者：魏云	--时间：2013-12-13--]]_G.CCollectJewelLevelSystemDB = {};function CCollectJewelLevelSystemDB:new(objDB,objUser)	local obj = {};	obj.objDBQuery = objDB;	obj.objUser = objUser;   --对应的玩家	for i, v in pairs(CCollectJewelLevelSystemDB) do		if type(v) == "function" then			obj[i] = v;		end;	end;	return obj;end----插入信息function CCollectJewelLevelSystemDB:InsertCollectJewelLevelInfo()	local objPlayer = self.objUser:GetPlayer()	if(not objPlayer) then		return false;	end	local SqlCmd = self.objDBQuery:CreateInsertCmd('T_Role_CollectJewelLevel'); 	SqlCmd.Wheres.dwRoleID  = objPlayer:GetRoleID(); 	SqlCmd.Fields.dwCollectJewelState = self.objUser:UpdateStr(self.objUser.CollectJewelState);	SqlCmd.Fields.dwCollectJewelLevel = self.objUser:UpdateStrs(self.objUser.CollectJewelLevel);	SqlCmd:execute()	return true;end----更新 信息function CCollectJewelLevelSystemDB:UpdateCollectJewelLevelInfo()	local objPlayer = self.objUser:GetPlayer()	if(not objPlayer) then		return false;	end	local SqlCmd = self.objDBQuery:CreateUpdateCmd('T_Role_CollectJewelLevel');	SqlCmd.Wheres.dwRoleID  = objPlayer:GetRoleID(); 	SqlCmd.Fields.dwCollectJewelState = self.objUser:UpdateStr(self.objUser.CollectJewelState);	SqlCmd.Fields.dwCollectJewelLevel = self.objUser:UpdateStrs(self.objUser.CollectJewelLevel);	SqlCmd:execute();	return true;end