--[[功能：数据中心数据库操作版本：v1.0作者：郭肇义时间：2012-04-14]]_G.CDataCenterSystemDB = {}---------------------------------------------------------------------------------------------function CDataCenterSystemDB:new(objDB,objSys)	local obj = {}	obj.objDB = objDB	obj.objSys = objSys		for i,v in pairs(CDataCenterSystemDB) do		if type(v) == "function" then obj[i] = v end	end	return objend;-----------------------------------------------------------------------------------------------接口---------------------------------------------------------------------------------------------function CDataCenterSystemDB:SetData(szIndex,uValue)	local dwRoleID = self.objSys:GetPlayer():GetRoleID()		local SqlCmd = self.objDB:CreateUpdateCmd('T_Role_DataCenter')	SqlCmd.Wheres.dwRoleID = dwRoleID	SqlCmd.Fields[szIndex] = uValue	SqlCmd:execute()end---------------------------------------------------------------------------------------------