function CDropControlEx:IsFit(cfg,dwMonsterID,dwLevel,dwType,dwMapID)	local dwNow = _now(1)	if (cfg.dwStartTime > dwNow) or (cfg.dwEndTime < dwNow) then--是否满足时间需求		return false;	end		if (cfg.Level[1] > dwLevel) or (cfg.Level[2] < dwLevel) then--是否满足等级需求		return false;	end		if not (#cfg.MonsterID==0) then--是否满足id需求		local bIn = false		for k,v in pairs(cfg.MonsterID) do			if v==dwMonsterID then				bIn = true;				break;			end		end		if not bIn then			return false;		end	end		if not (#cfg.Type==0) then--是否满足类型需求		local bIn = false		for k,v in pairs(cfg.Type) do			if v==dwType then				bIn = true;				break;			end		end		if not bIn then			return false;		end	end		if not (#cfg.Map==0) then--是否满足地图需求		local bIn = false		for k,v in pairs(cfg.Map) do			if v==dwMapID then				bIn = true;				break;			end		end		if not bIn then			return false;		end	end		return true;endfunction CDropControlEx:Check(Monster)	local cfg = DropExConfig or {}	local dwMonsterID = Monster:GetID()	local dwLevel = Monster:GetLevel()	local dwMapID = Monster:GetMap().MapID	local dwType = Monster:GetMonsterType()	if Monster:IsVariation() then		dwType = 3	end	local DropTable = {}		for k,v in pairs(cfg) do		if self:IsFit(v,dwMonsterID,dwLevel,dwType,dwMapID) then			--print(dwMonsterID,k,v.dwDropID)			table.insert(DropTable,v.dwDropID)		end	end		return DropTableend