--升级侠义勋章星值define.ReqUpErrantHonorMsg{	user = NONE, }when{ user = NONE }ReqUpErrantHonorMsg = checkUserwhen{ }function ReqUpErrantHonorMsg(user)	local objErrantHonorSystem = user:GetSystem("CErrantHonorSystem");	if not objErrantHonorSystem then		return;	end;	objErrantHonorSystem:UpErrantHonor();end;--侠义勋章广播define.ErrantHonorSys_BroadCastMsg{	PlayerName = "",    PlayerRoleID = 0,	Info = {},}when{}function ErrantHonorSys_BroadCastMsg(PlayerName, PlayerRoleID, Info)	local setNet = {};	for i,objPlayer in pairs(CPlayerManager.setAllPlayer) do		table.insert(setNet, objPlayer.objNet);	end;	_groupcallout(setNet, "ErrantHonorBroadCastMsg", {RoleID = PlayerRoleID, RoleName = PlayerName, Info = Info});	end;--查看对方侠义勋章define.ErrantSys_ViewOtherMsg{	user = NONE,	RoleID = 0, }when{ user = NONE }ErrantSys_ViewOtherMsg = checkUserwhen{ }function ErrantSys_ViewOtherMsg(user, RoleID)	local objErrantHonor = user:GetSystem("CErrantHonorSystem");	if not objErrantHonor then		return;	end;	objErrantHonor:ViewOtherErrantHonor(RoleID);end; 