define.ReqErrantAwardMsg{	user = NONE, 	Pos = 0,}when{ user = NONE }ReqErrantAwardMsg = checkUserwhen{ }function ReqErrantAwardMsg(user, Pos)	local errantSystem = user:GetSystem("CErrantSystem");	if not errantSystem then		return;	end;	errantSystem:GetErrantAward(Pos);end;define.ErrantSys_BroadCastMsg{	PlayerName = "",    PlayerRoleID = 0,    Type = 0,    MapId = 0,	MonsterId = 0,}when{}function ErrantSys_BroadCastMsg(PlayerName, PlayerRoleID, Type, MapId, MonsterId)	--local n_BroadCastLevel = _G.BroadCastConfig.StoneCompose;	local setNet = {};	for i,objPlayer in pairs(CPlayerManager.setAllPlayer) do		--if objPlayer:GetInfo().dwLevel >= n_BroadCastLevel then			table.insert(setNet, objPlayer.objNet);		--end;	end;	_groupcallout(setNet, "ErrantSys_BroadCastMsg", {PlayerName = PlayerName, PlayerRoleID = PlayerRoleID, Type = Type, MapId = MapId, MonsterId = MonsterId});	end;