local function func(objPlayer,tbInfoList,isNewRole)	local dwAccountID = objPlayer:GetAccountID();	local dwRoleID = objPlayer:GetRoleID();    local tabData = {};    local dbres1 = tbInfoList[1];    if #(dbres1) > 0 then		tabData = dbres1[1];    else		local SqlCmd = objPlayer:GetDBQuery():CreateInsertCmd('T_Role_Cross_Info');		SqlCmd.Fields.dwRoleId	= dwRoleID;		SqlCmd:execute();			tabData.dwHonor = 0;	end;			CTransitManager:RecvData(dwAccountID,"CCrossSvrSystem", tabData);	return true;end;CLoginCall:AddCall("CCrossSvrSystem",func);