local function func(objPlayer,tbInfoList,isNewRole)	local dwAccountID = objPlayer:GetAccountID();	local dwRoleID = objPlayer:GetRoleID();	    local lineData = {};    local tabData = {};	local tabData1 = {};    local dbres1 = tbInfoList[1];    if #(dbres1) > 0 then		tabData = dbres1;    else		local SqlCmd = objPlayer:GetDBQuery():CreateInsertCmd('T_Equip_Build_Info');		SqlCmd.Fields.dwRoleId		= dwRoleID;		SqlCmd.Fields.szBuildInfo	= "";		SqlCmd.Fields.dwSuitLevel	= 0;		SqlCmd:execute();	 		local tbl = {};         		tbl.szBuildInfo = 0;    		tbl.dwSuitLevel = 0;  					table.insert(tabData, tbl);	end;	    local dbres2 = tbInfoList[2];    if #(dbres2) > 0 then		tabData1 = dbres2;    else		local SqlCmd = objPlayer:GetDBQuery():CreateInsertCmd('T_Role_EquipBuild_Info');		SqlCmd.Fields.dwRoleId = dwRoleID;		SqlCmd:execute()		local tbl = {};         		tbl.dwCritBless = 0;    		tbl.dwBuildNum = 0;  				table.insert(tabData1, tbl);    end;		table.insert(lineData, tabData[1]);	table.insert(lineData, tabData1[1]);	CTransitManager:RecvData(dwAccountID,"CEquipBuildSystem", lineData);	return true;end;CLoginCall:AddCall("CEquipBuildSystem",func);