local function func(objPlayer,tbInfoList,isNewRole)	local dwAccountID = objPlayer:GetAccountID();	local dwRoleID = objPlayer:GetRoleID();	    --从数据库中读取系统信息    local data1 = {};	local res1 = tbInfoList[1]    if #(res1) > 0 then		data1[1] = res1[1].dwOnlineTime;		data1[2] = res1[1].szAward;    else        data1[1] = 0;		local str = "";		local formatStr = "%d,%d;"		for i=1, _G.OnLineGiftConfig.GiftNum do			local str1 = string.format(formatStr, i, 0);			str = str .. str1;		end;		data1[2] = str;		local SqlCmd = objPlayer:GetDBQuery():CreateInsertCmd("T_Role_OnlineGift");        SqlCmd.Fields.dwRoleID		= dwRoleID;        SqlCmd.Fields.dwOnlineTime	= data1[1];        SqlCmd.Fields.szAward		= data1[2];        SqlCmd:execute();    end;	CTransitManager:RecvData(dwAccountID,"COnlineGiftSystem", data1);	return true;end;CLoginCall:AddCall("COnlineGiftSystem",func);