local function func(objPlayer,tbInfoList,isNewRole)	local dwAccountID = objPlayer:GetAccountID();	local dwRoleID = objPlayer:GetRoleID();    local lineData = {};    local tabData = {};    local dbres1 = tbInfoList[1];    if #(dbres1) > 0 then		tabData = dbres1[1];    else		local SqlCmd = objPlayer:GetDBQuery():CreateInsertCmd('T_Practice_Info');		SqlCmd.Fields.dwRoleId		= dwRoleID;		SqlCmd:execute();			tabData.dwCurLevel = 0;		tabData.dwCurProgress = 0;		tabData.dwCurHideLevel = 0;	end;			table.insert(lineData, tabData);	CTransitManager:RecvData(dwAccountID,"CPracticeSystem", lineData);	return true;end;CLoginCall:AddCall("CPracticeSystem",func);