local function func(objPlayer,tbInfoList,isNewRole)	local dwAccountID = objPlayer:GetAccountID();	local dwRoleID = objPlayer:GetRoleID();	    --从数据库中读取系统信息    local data = {};	local res = tbInfoList[1]    if #(res) > 0 then		data[1] = res[1].times;		data[2] = res[1].awards;		data[3] = res[1].selects;    else        data[1] = _G.QihunConfig.dwRandomTimes;		data[2] = "";		data[3] = "";		local SqlCmd = objPlayer:GetDBQuery():CreateInsertCmd("t_role_qihun");        SqlCmd.Fields.roleid	= dwRoleID;        SqlCmd.Fields.times		= data[1];        SqlCmd.Fields.awards	= data[2];		SqlCmd.Fields.selects	= data[3];        SqlCmd:execute();    end;	CTransitManager:RecvData(dwAccountID, "CQihunSystem", data);	return true;end;CLoginCall:AddCall("CQihunSystem",func);