local function func(objPlayer,tbInfoList,isNewRole)	local dwAccountID = objPlayer:GetAccountID();	local dwRoleID = objPlayer:GetRoleID();	    local lineData = {};    local tabData = {};    local dbres1 = tbInfoList[1];    if #(dbres1) > 0 then		tabData = dbres1[1];    else		local SqlCmd = objPlayer:GetDBQuery():CreateInsertCmd('T_Iron_Cloth_Info');		SqlCmd.Fields.dwRoleId		= dwRoleID;		SqlCmd:execute();			tabData.dwClothLevel = 1;		tabData.dwClothExp = 0;		tabData.dwForbidValue = 0;		tabData.dwSuitLevel = 0;		end;			table.insert(lineData, tabData);	CTransitManager:RecvData(dwAccountID,"CTieBuShanSystem", lineData);	return true;end;CLoginCall:AddCall("CTieBuShanSystem",func);