define.MegaKill_BroadCastAptitudeMsg{	PlayerRoleID = 0;	PlayerName = "";	dwAptitude = 0;}when{}function MegaKill_BroadCastAptitudeMsg(PlayerRoleID, PlayerName, dwAptitude)	local setNet = {};	for i,objPlayer in pairs(CPlayerManager.setAllPlayer) do		table.insert(setNet, objPlayer.objNet);	end;	_groupcallout(setNet, "MegaKill_BroadCastAptitudeMsg", {PlayerRoleID = PlayerRoleID, PlayerName = PlayerName, dwAptitude = dwAptitude});	end;