--[[	提示 系统（服务器）	周长沙	2012-4-12--]]-------------------------------------_G.CNoticeSystem = {}-------------------------------------function CNoticeSystem:new()	local obj = CSystem:new("CNoticeSystem");	for i,v in pairs(CNoticeSystem) do		if type(v) == "function" then			obj[i] = v;		end;	end;	return obj;endfunction CNoticeSystem:Create()	return true;endfunction CNoticeSystem:Update(dwInterval)	return trueendfunction CNoticeSystem:Destroy()	return trueend----------------------------------------出错提示function CNoticeSystem:ErrNotice(dwId)	local objPlayer = self:GetPlayer()	if not objPlayer then return end;	objPlayer.TaskErrorMsg{Id=dwId}end--副本提示function CNoticeSystem:FBNotice(dwId)	local objPlayer = self:GetPlayer()	if not objPlayer then return end;	objPlayer.NoticeSystemNoticeIDMsg{Type=7,Id=dwId}end--类型提示1function CNoticeSystem:Notice(dwType,dwId)	local objPlayer = self:GetPlayer()	if not objPlayer then return end;	objPlayer.NoticeSystemNoticeIDMsg{Type=dwType,Id=dwId}end--类型提示2function CNoticeSystem:NoticeStr(dwType,szStr)	local objPlayer = self:GetPlayer()	if not objPlayer then return end;	objPlayer.NoticeSystemNoticeStrMsg{Type=dwType,Str=szStr}end