--[[功能：新手引导系统消息处理（服务器）版本：v1.0作者：郭肇义时间：2012-03-22]]_define()--客户端请求已触发引导列表define.GetFinishedNoviceGuideListMsg{	user = NONE,}when{user = NONE}GetFinishedNoviceGuideListMsg = checkUser;when{}function GetFinishedNoviceGuideListMsg(user)	local objNoviceSystem = user:GetSystem("CNoviceSystem");	local tabGuideList = objNoviceSystem:GetFinishedNoviceGuideList();	user.OnGetFinishedNoviceGuideListMsg{ GuideList = tabGuideList or {} }end;--客户端注册普通事件define.RegisterNoviceGuideCommonEventMsg{	user = NONE,	EventList = {},}when{user = NONE}RegisterNoviceGuideCommonEventMsg = checkUser;when{}function RegisterNoviceGuideCommonEventMsg(user, EventList)	local objNoviceSystem = user:GetSystem("CNoviceSystem");	objNoviceSystem:RegisterNoviceGuideCommonEvent(EventList);end;--客户端注册任务事件define.RegisterNoviceGuideTaskEventMsg{	user = NONE,	EventList = {},}when{user = NONE}RegisterNoviceGuideTaskEventMsg = checkUser;when{}function RegisterNoviceGuideTaskEventMsg(user, EventList)	local objNoviceSystem = user:GetSystem("CNoviceSystem");	objNoviceSystem:RegisterNoviceGuideTaskEvent(EventList);end;--客户端注册升级事件define.RegisterNoviceGuideLevelEventMsg{	user = NONE,	EventList = {},}when{user = NONE}RegisterNoviceGuideLevelEventMsg = checkUser;when{}function RegisterNoviceGuideLevelEventMsg(user, EventList)	local objNoviceSystem = user:GetSystem("CNoviceSystem");	objNoviceSystem:RegisterNoviceGuideLevelEvent(EventList);end;--客户端注册引导完成define.RegisterNoviceGuideFinishedMsg{	user = NONE,	GuideId = 0,}when{user = NONE}RegisterNoviceGuideFinishedMsg = checkUser;when{}function RegisterNoviceGuideFinishedMsg(user, GuideId)	if not CConfig.bIsCrossSvr then		local objNoviceSystem = user:GetSystem("CNoviceSystem");		objNoviceSystem:RegisterNoviceGuideFinished(GuideId);	endend;