--[[	--功能：童姥功数据库类	--作者：魏云	--时间：2013-12-20--]]_G.CPractiseSkillSystemDB = {};function CPractiseSkillSystemDB:new(objDB,objUser)	local obj = {};	obj.objDBQuery = objDB;	obj.objUser = objUser;   --对应的玩家	for i, v in pairs(CPractiseSkillSystemDB) do		if type(v) == "function" then			obj[i] = v;		end;	end;	return obj;end----插入信息function CPractiseSkillSystemDB:InsertPractiseSkillInfo()	local objPlayer = self.objUser:GetPlayer()	if(not objPlayer) then		return false;	end	local SqlCmd = self.objDBQuery:CreateInsertCmd('T_Role_PractiseSkill'); 	SqlCmd.Wheres.dwRoleID  = objPlayer:GetRoleID(); 	SqlCmd.Fields.dwPractiseLevel = self.objUser.PractiseLevel;	SqlCmd.Fields.dwMendValue = self.objUser.MendValue;	SqlCmd.Fields.dwStarLevel = self.objUser.StarLevel;	SqlCmd.Fields.dwHeadState = self.objUser.HeadState;	SqlCmd:execute()	return true;end----更新 信息function CPractiseSkillSystemDB:UpdatePractiseSkillInfo()	local objPlayer = self.objUser:GetPlayer()	if(not objPlayer) then		return false;	end	local SqlCmd = self.objDBQuery:CreateUpdateCmd('T_Role_PractiseSkill');	SqlCmd.Wheres.dwRoleID  = objPlayer:GetRoleID(); 	SqlCmd.Fields.dwPractiseLevel = self.objUser.PractiseLevel;	SqlCmd.Fields.dwMendValue = self.objUser.MendValue;	SqlCmd.Fields.dwStarLevel = self.objUser.StarLevel;	SqlCmd.Fields.dwHeadState = self.objUser.HeadState;	SqlCmd:execute();	return true;end