--[[   [ \brief 请求摇奖消息   [ \param Number nIndex 摇奖序号  -- 为 0 代表全部刷新   ]]define.ReqQingYunLotteryMsg{	user = NONE, 	nIndex = 0,	nCount = 1;}when{ user = NONE }ReqQingYunLotteryMsg = checkUserwhen{ }function ReqQingYunLotteryMsg(user,nIndex,nCount)	local objQingYunLingSystem = user:GetSystem("CQingYunLingSystem");	if not objQingYunLingSystem then		return;	end;	objQingYunLingSystem:OnLottery(nIndex,nCount);end;--[[   [ \brief 请求领奖消息   ]]define.ReqQingYunGetPrizeMsg{	user = NONE, }when{ user = NONE }ReqQingYunGetPrizeMsg = checkUserwhen{ }function ReqQingYunGetPrizeMsg(user)	local objQingYunLingSystem = user:GetSystem("CQingYunLingSystem");	if not objQingYunLingSystem then		return;	end;	objQingYunLingSystem:OnGetPrize();end;--侠义勋章广播define.QingYunLingSys_BroadCastMsg{	sName = "",	nRoleID = 0,	nLadder = 0,}when{}function QingYunLingSys_BroadCastMsg(sName, nRoleID, nLadder)	local setNet = {};	for i,objPlayer in pairs(CPlayerManager.setAllPlayer) do		table.insert(setNet, objPlayer.objNet);	end;	_groupcallout(setNet, "QingYunLingBroadCastMsg", {RoleID = nRoleID, RoleName = sName, nLadder = nLadder});	end;--查看对方侠义勋章define.CloudMedalSys_ViewOtherMsg{	user = NONE,	RoleID = 0, }when{ user = NONE }CloudMedalSys_ViewOtherMsg = checkUserwhen{ }function CloudMedalSys_ViewOtherMsg(user, RoleID)	local objQingYunLingSystem = user:GetSystem("CQingYunLingSystem");	if not objQingYunLingSystem then		return;	end;	objQingYunLingSystem:ViewOtherErrantHonor(RoleID);end; 