_G.CRefreshSystemDB = {}-----------------------------------------------------------------------------------------------function CRefreshSystemDB:new(objPlayer)	local obj = {}	obj.objDB = objPlayer:GetDBQuery();	obj.dwRoleID = objPlayer:GetRoleID();	for i, v in pairs(CRefreshSystemDB) do		if type(v) == "function" then			obj[i] = v;		end;	end;	return obj;end;-----------------------------------------------------------------------------------------------function CRefreshSystemDB:AddData(dwDataID,dwValue)	local SqlCmd = self.objDB:CreateInsertCmd('T_Role_Refresh');	SqlCmd.Fields.dwRoleID = self.dwRoleID;	SqlCmd.Fields.dwDataID = dwDataID;	SqlCmd.Fields.dwValue = dwValue;	SqlCmd.Fields.dwUpdate = GetCurTime();	SqlCmd:execute();endfunction CRefreshSystemDB:SetData(dwDataID,dwValue)	local SqlCmd = self.objDB:CreateUpdateCmd('T_Role_Refresh');	SqlCmd.Fields.dwValue = dwValue;	SqlCmd.Fields.dwUpdate = GetCurTime();	SqlCmd.Wheres.dwRoleID = self.dwRoleID;	SqlCmd.Wheres.dwDataID = dwDataID;	SqlCmd:execute();endfunction CRefreshSystemDB:DelData(dwDataID)	local SqlCmd = self.objDB:CreateDeleteCmd('T_Role_Refresh');	SqlCmd.Wheres.dwRoleID = self.dwRoleID;	SqlCmd.Wheres.dwDataID = dwDataID;	SqlCmd:execute();end-----------------------------------------------------------------------------------------------