--[[功能：服务器大事件系统消息处理（服务器）版本：v1.0作者：粟宇征时间：2012-12-05 ]]_define()--[[--客户端大事件存储请求define.WriteSeverEventDataMsg{	user = NONE,	TabInfo = {},}when{user = NONE}WriteSeverEventDataMsg = checkUser;when{}function WriteSeverEventDataMsg(user,TabInfo)--	local objSystem = user:GetSystem("CServerEventSystem");	CKernelApp.KSRequestSeverEventDataMsg{ ResID = 	user:GetInfo().dwRoleID,Data = TabInfo }end;]]--从KS缓冲中获取数据define.OnKSGetSeverEventDataMsg{	user = NONE,	TabInfo = {},}when{user = NONE}OnKSGetSeverEventDataMsg = checkKSUser;when{}function OnKSGetSeverEventDataMsg(user,TabInfo)	local objSystem = user:GetSystem("CServerEventSystem");	objSystem:SetSeverEventData(TabInfo.tabData);	objSystem:AddSeverEventData(TabInfo.Data);end;--客户端大事件所有数据请求define.SeverEventDataMsg{	user = NONE,	TabInfo = {},}when{user = NONE}SeverEventDataMsg = checkUser;when{}function SeverEventDataMsg(user)	local objSystem = user:GetSystem("CServerEventSystem");	objSystem:SeverEventData();end;--客户端大事件类型数据请求define.SeverEventTypeDataMsg{	user = NONE,	eSeverEventType = 0,}when{user = NONE}SeverEventTypeDataMsg = checkUser;when{}function SeverEventTypeDataMsg(user,eSeverEventType)	local objSystem = user:GetSystem("CServerEventSystem");	objSystem:SeverEventTypeData(eSeverEventType);end;