--[[	剧情系统 网络消息（服务器）	周长沙	2012-4-12--]]_define()-------------------------------------------------------收到某个剧情结束的消息define.StoryFinishMsg{	user = NONE;	StoryId = 0;}when{user = NONE}StoryFinishMsg = checkUserwhen{}function StoryFinishMsg(user,StoryId)	local sysStory = user:GetSystem("CStorySystem")	if sysStory then		sysStory:StoryFinishMsg(StoryId)	endend--收到某个场景展示结束define.StoryScnFinishMsg{	user = NONE;	StoryId = 0;}when{user = NONE}StoryScnFinishMsg = checkUserwhen{}function StoryScnFinishMsg(user,StoryId)	local sysStory = user:GetSystem("CStorySystem")	if sysStory then		sysStory:ScnFinishMsg(StoryId)	endend