--[[	名剑注灵系统消息	魏云	2013-10-17]]-----名剑注灵define.SetSwordFoundryValueMsg{	user = NONE,	SwordEnemID = 0;   ------宝剑的配置ID	IsAddStone = false;------是否消耗石头}when{ user = NONE }SetSwordFoundryValueMsg = checkUserwhen{ }function SetSwordFoundryValueMsg(user,SwordEnemID,IsAddStone)	local swordFoundrySys = user:GetSystem("CSwordFoundrySystem");	if not swordFoundrySys then		return;	end	swordFoundrySys:SetSwordFoundry(SwordEnemID,IsAddStone);end; --名剑注灵广播define.SwordFoundryBroadCastMsg{	PlayerID = 0,	PlayerName ="",  -----玩家名字	StartNum = 0,};when{}function SwordFoundryBroadCastMsg(PlayerID,PlayerName,StartNum)	local setNet = {};	for i,objPlayer in pairs(CPlayerManager.setAllPlayer) do		table.insert(setNet, objPlayer.objNet);	end;	_groupcallout(setNet, "SwordFoundryBroadCastMsg", {PlayerID = PlayerID,PlayerName = PlayerName,StartNum = StartNum});	end