--[[尤物志全部提交]]define.YWZ_SubmitAllMsg{	user = NONE; 	dwImageID=0;}when{ user = NONE }YWZ_SubmitAllMsg = checkUserwhen{}function YWZ_SubmitAllMsg(user,dwImageID)	local ywzSystem = user:GetSystem("CYouWuZhiSystem");	if not ywzSystem then		return;	end;	ywzSystem:SubmitAll(dwImageID);end--[[	尤物志提交单个物品]]define.YWZ_SubmitItemMsg{	user = NONE;	dwImageID=0;	dwItemID =0;	dwNum=0;}when{ user = NONE }YWZ_SubmitItemMsg = checkUserwhen{}function YWZ_SubmitItemMsg(user,dwImageID,dwItemID,dwNum)	local ywzSystem = user:GetSystem("CYouWuZhiSystem");	if not ywzSystem then		return;	end;	ywzSystem:SubmitItem(dwImageID,dwItemID,dwNum);end