--[[	[ \brief 珍珑棋局排序管理器	[ \author 李中昌	[ \date 2014-5-28	[ 处理珍珑棋局排序	]]-- 管理器对象_G.CZhenlongChessChartManager = CSingle:new();CSingleManager:AddSingle(CZhenlongChessChartManager);-- 存储累计星级最高的玩家的信息 CZhenlongChessChartManager.tStarPlayerInfo = nil;function CZhenlongChessChartManager:Create()	return true;end	function CZhenlongChessChartManager:RequestInitInfoFromKS()	CKernelApp.OnRequestZhenlongStarMsg	{		LineID = CKernelApp.dwLineID;	}end;function CZhenlongChessChartManager:OnKSReConnect()	self:RequestInitInfoFromKS();end;function CZhenlongChessChartManager:OnKSConnect()	self:RequestInitInfoFromKS();end;			-- 更新星级最高的玩家信息	function CZhenlongChessChartManager:UpdateStarLevel(tPlayerInfo)	self.tStarPlayerInfo = tPlayerInfo;end	--得到玩家信息function CZhenlongChessChartManager:GetStarPlayInfo()	return self.tStarPlayerInfo;end