--[[功能：公会系统消息处理（服务器）版本：v1.0作者：郭肇义时间：2012-05-04]]_define()--客户端请求玩家IDdefine.KSGetAchievementPlayerIDMsg{	user = NONE,	RoleID = 0,	RoleName = '',}when{}function KSGetAchievementPlayerIDMsg(RoleID,RoleName)	local objPlayer = CGameLineManager:GetPlayer(RoleID)	if not objPlayer then return end	local dwPlayerID = nil	local tabPlayerList = CGameLineManager:GetPlayer();	for dwRoleID, objPlayer in pairs(tabPlayerList) do		local tabInfo = objPlayer:GetInfo()		if tabInfo.szRoleName == RoleName then			dwPlayerID = dwRoleID			break		end	end	objPlayer.OnKSGetAchievementPlayerIDMsg {RoleID = dwPlayerID}end;--初始化隐藏成就列表define.KSInitHideAchievementListMsg{	LineID = 0,}when{}function KSInitHideAchievementListMsg(LineID)	local tabData = CAchievementManager:GetHideAchievementList()	local objLine = CGameLineManager:GetLine(LineID)	objLine.OnKSInitHideAchievementListMsg{AchievementList = tabData}end;--添加隐藏成就define.KSAddHideAchievementMsg{	AchievementID = 0,	RoleID = 0,	RoleName = '',}when{}function KSAddHideAchievementMsg(AchievementID,RoleID,RoleName)	local bRes = CAchievementManager:AddHideAchievement(AchievementID,RoleID,RoleName);	if not bRes then return end;	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.OnKSAddHideAchievementMsg{			AchievementID = AchievementID, RoleID = RoleID, RoleName = RoleName,		};	end;end;--define.KSMedalNoticeMsg{	RoleID = 0;	RoleName = "";	ButtonIndex = 0;}when{}function KSMedalNoticeMsg(RoleID,RoleName,ButtonIndex)	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.GSRemoteCallMsg		{			System = "CAchievementSystem",			Func = "SendMedalNotice",			Args = {RoleID,RoleName,ButtonIndex},		};	end;end;