_G.CAddAttrMgr = CSingle:new();CSingleManager:AddSingle(CAddAttrMgr);--createfunction CAddAttrMgr:Create()		--初始化计时器	CAlarmClock:AddTask({dwTime = "0:0"}, CAlarmTaskCycleType.eDay, 1, 0,		function() self:ResetValue() end, {}	)	return true;end;--重置function CAddAttrMgr:ResetValue()	CPlayerDBQuery:GetDBQuery():execute('UPDATE "t_role_addattrs" SET "records" = $1 WHERE "records" != $1', "");	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.GSRemoteCallMsg{System = "CAddAttrSystem", Func = "Reset"};	end;	end;