--[[功能：评价系统类（服务器）版本：v1.0作者：郭肇义时间：2012-04-05]]_G.CEvaluateManager = CSingle:new();CSingleManager:AddSingle(CEvaluateManager);---------------------------------------------------------------------------------------------function CEvaluateManager:Create()	--初始化DB	local objDB = CPlayerDBQuery:GetDBQuery()	if not objDB then		_err("CEvaluateCache Create Cannot get DBQuery");		return false;	end;	self.objDBQuery = CEvaluateManagerDB:Create(objDB);	--初始化评价列表	self.tabEvaluateList = self.objDBQuery:GetEvaluateList();	return true;end;function CEvaluateManager:Update(dwInterval)	return true;end;function CEvaluateManager:Destroy()end;-----------------------------------------------------------------------------------------------接口-----------------------------------------------------------------------------------------------发送评价function CEvaluateManager:GetEvaluate(dwRoleID)	return self.tabEvaluateList[dwRoleID]end--收到评价function CEvaluateManager:AddEvaluate(dwRoleID,eType)	local tabEvaluate = self.tabEvaluateList[dwRoleID]	if not tabEvaluate then		tabEvaluate = { dwFlower = 0, dwEgg = 0 }		self.tabEvaluateList[dwRoleID] = tabEvaluate		self.objDBQuery:AddEvaluate(dwRoleID,tabEvaluate)	end	if eType == 0 then		tabEvaluate.dwFlower = tabEvaluate.dwFlower + 1	end	if eType == 1 then		tabEvaluate.dwEgg = tabEvaluate.dwEgg + 1	end	self.objDBQuery:SetEvaluate(dwRoleID,tabEvaluate)end