_G.CFiveElementMgr = CSingle:new();CSingleManager:AddSingle(CFiveElementMgr);--createfunction CFiveElementMgr:Create()		--初始化计时器	CAlarmClock:AddTask({dwTime = "24:00"}, CAlarmTaskCycleType.eDay, 1, 0,		function() self:ResetValue() end, {}	)	return true;end;--updatefunction CFiveElementMgr:Update(dwInterval)	return true;end;--destroyfunction CFiveElementMgr:Destroy()end;------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------对外接口--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------重置function CFiveElementMgr:ResetValue()	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.GSRemoteCallMsg{System = "CFiveElemSystem", Func = "ResetInfo"};	end;	end;