------------------------------------------------------------------------------------奖杯更新function CGuildServerMsg:UpdateTrophy(tabData)	local tabModify = {}	for dwTrophyID,dwGuildID in pairs(tabData) do		local objTrophy = CGuildTrophyMgr:Get(dwTrophyID)		if not objTrophy then			local tabRefresh = tabModify[dwGuildID] or {}			table.insert(tabRefresh,{ dwTrophyID,true })			tabModify[dwGuildID] = tabRefresh			objTrophy = CGuildTrophy:new()			objTrophy.dwTrophyID = dwTrophyID			CGuildTrophyMgr:Add(objTrophy):Insert()		end		if objTrophy.dwGuildID ~= dwGuildID then			local tabRefresh = tabModify[objTrophy.dwGuildID] or {}			table.insert(tabRefresh,{ dwTrophyID,nil })			tabModify[objTrophy.dwGuildID] = tabRefresh			CGuildTrophyMgr:Set(dwTrophyID,dwGuildID):Update()			local tabRefresh = tabModify[objTrophy.dwGuildID] or {}			table.insert(tabRefresh,{ dwTrophyID,true })			tabModify[objTrophy.dwGuildID] = tabRefresh		end	end	CGuildTrophyMgr:Sort()	for dwGuildID in pairs(tabModify) do		if not CGuildInfoMgr:Get(dwGuildID) then			tabModify[dwGuildID] = nil		end	end	for dwGuildID,tabRefresh in pairs(tabModify) do		CGuildPlayerMsg:NotifyGuild(			dwGuildID,'OnRefreshTrophyList',tabRefresh		)	end	local tabOrder = CGuildTrophyMgr:GetOrder()	for _,dwGuildID in pairs(tabOrder) do		local tabList = CGuildTrophyMgr:GetTrophyList(dwGuildID)		local dwCount,dwExp = 0,0		for dwTrophyID in pairs(tabList) do			local tabCfg = GuildTrophyConfig[dwTrophyID]			tabCfg = GuildTrophyType[tabCfg.dwTrophyType]			dwCount = dwCount + 1			dwExp = dwExp + tabCfg.dwAddExp		end		if dwCount ~= 0 and dwExp ~= 0 then			local eChannel = enChatChannel.eFaction			CGuildPlayerMsg:NotifyGuild(				dwGuildID,'Message',eChannel,6001010077,dwCount,dwExp			)		end	endend----------------------------------------------------------------------------------