_G.CMuseMgr = CSingle:new();CSingleManager:AddSingle(CMuseMgr);--createfunction CMuseMgr:Create()	--初始化计时器	CAlarmClock:AddTask({dwTime = InjectConfig.szResetTime}, CAlarmTaskCycleType.eDay, 1, 0,		function() self:ResetValue() end, {}	)	return true;end;------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------对外接口--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------重置function CMuseMgr:ResetValue()	--DB	CPlayerDBQuery:GetDBQuery():execute('DELETE FROM "T_Role_Inject_Log"');	CPlayerDBQuery:GetDBQuery():execute('UPDATE "T_Role_Muse" SET "dwBeInjectCount"=0, "dwMyInjectExpCount"=0, "dwGiftCount"=0');	CPlayerDBQuery:GetDBQuery():execute('UPDATE "T_Role_Friend" SET "dwInjectFlag"=0 WHERE "dwInjectFlag"!=0');		--online	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.GSRemoteCallMsg{System = "CMuseSystem", Func = "ClearInjectInfo"};	end;	end;