_G.CPulseMgr = CSingle:new();CSingleManager:AddSingle(CPulseMgr);--createfunction CPulseMgr:Create()		--初始化计时器	CAlarmClock:AddTask({dwTime = "24:00"}, CAlarmTaskCycleType.eDay, 1, 0,		function() self:ResetValue() end, {}	)	return true;end;--updatefunction CPulseMgr:Update(dwInterval)	return true;end;--destroyfunction CPulseMgr:Destroy()end;------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------对外接口--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------重置function CPulseMgr:ResetValue()	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.GSRemoteCallMsg{System = "CPulseSystem", Func = "ResetInfo"};	end;	end;