_G.CQihunMgr = CSingle:new();CSingleManager:AddSingle(CQihunMgr);--createfunction CQihunMgr:Create()		--初始化计时器	CAlarmClock:AddTask({dwTime = _G.QihunConfig.ResetTime}, CAlarmTaskCycleType.eDay, 1, 0,		function() self:ResetValue() end, {}	)	return true;end;--重置function CQihunMgr:ResetValue()	CPlayerDBQuery:GetDBQuery():execute('UPDATE "t_role_qihun" SET "times" = $1 WHERE "times" != $1', _G.QihunConfig.dwRandomTimes);	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.GSRemoteCallMsg{System = "CQihunSystem", Func = "Reset"};	end;	end;