_G.CVipMgr = CSingle:new();CSingleManager:AddSingle(CVipMgr);--createfunction CVipMgr:Create()		--初始化计时器	CAlarmClock:AddTask({dwTime = "24:00"}, CAlarmTaskCycleType.eDay, 1, 0,		function() self:ResetValue() end, {}	)	return true;end;--updatefunction CVipMgr:Update(dwInterval)	return true;end;--destroyfunction CVipMgr:Destroy()end;------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------对外接口--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------重置function CVipMgr:ResetValue()     CPlayerDBQuery:GetDBQuery():execute('update "T_Role_Vip" set "dwGetItem" = $1', 0);	for i,objLine in pairs(CGameLineManager:GetAllLine()) do		objLine.GSRemoteCallMsg{System = "CVipSystem", Func = "ResetInfo"};	end;	end;